  test "raises on mix styles" do
    assert_raise ArgumentError, "expected either line or fill, but got both.", fn ->
      render_component(&Remixicon.github/1, %{fill: true, line: true})
    end
  end

  test "generated docs" do
    {:docs_v1, _annotation, _beam_language, _format, _module_doc, _metadata, docs} =
      Code.fetch_docs(Remixicon)

    doc =
      Enum.find_value(docs, fn
        {{:function, :github, 1}, _annotation, _signature, doc, _metadata} -> doc
        _ -> nil
      end)

    assert doc["en"] == """
           Renders the `github` icon.

           By default, the lined component is used, but the `fill`
           attribute can be provided for alternative styles.

           You may also pass arbitrary HTML attributes to be applied to the svg tag.

           ## Examples

           ```heex
           <Remixicon.github />
           <Remixicon.github class="w-4 h-4" />
           <Remixicon.github line />
           <Remixicon.github fill />
           ```

           ## Attributes

           * `line` (`:boolean`) - Defaults to `false`.
           * `fill` (`:boolean`) - Defaults to `false`.
           Global attributes are accepted.
           """
  end
end
